import { Component } from '@angular/core';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent {
  @ViewChild('deleteModal') deleteModal!: ModalComponent;

  openDeleteModal() { this.deleteModal.open(); }

  deleteTask() {
    this.deleteModal.close();
    this.notificationService.show('Task deleted successfully!', 'success');
  }

}
<app-button label="Delete Task" type="danger" (action)="openDeleteModal()"></app-button>

<app-modal #deleteModal title="Are you sure?" (confirm)="deleteTask()" (cancel)="deleteModal.close()">
  <p>Do you really want to delete this task?</p>
</app-modal>
