import { Component, ViewChild, ViewContainerRef } from '@angular/core';
import { NotificationService } from './services/notification.service';
import { SurveyServiceService } from './services/survey-service.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'advanced-component-demo';

  @ViewChild('surveyContainer', {read : ViewContainerRef}) container! : ViewContainerRef;

  constructor(private surveyService : SurveyServiceService) { }

  loadTextQuestion() {
  }

  loadMultipleChoice() {
    this.surveyService.loadQuestion('multiple', 'Which languages do you speak?', ['English', 'Spanish', 'French']);
  }

  loadDropdown() {
    this.surveyService.loadQuestion('dropdown', 'Select your country:', ['USA', 'UK', 'Canada']);
  }


}




<button (click)="loadTextQuestion()"  >Load text Question</button>
<button (click)="loadMultipleChoice()">Load Multiple Choice</button>
<button (click)="loadDropdown()">Load Dropdown</button>

