// sync

import { AbstractControl, ValidationErrors } from "@angular/forms";

export function restrictedEmailDomain(control : AbstractControl) : ValidationErrors | null {
    const email = control.value;
    if(email && email.endsWith('@test.com')){
        return {restrictedDomain : true}
    }

    return null;
}
