import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-registration-form',
  templateUrl: './registration-form.component.html',
  styleUrls: ['./registration-form.component.scss']
})
export class RegistrationFormComponent {
  registrationForm! : FormGroup;

  constructor(private fb : FormBuilder){
    this.registrationForm = this.fb.group({
      username : ['', [Validators.required, Validators.minLength(3)]],
      email : ['', [Validators.required, Validators.email]],
      password : ['', [Validators.required, Validators.minLength(6)]],
      addresses : this.fb.array([]) // dynmic address
    })
  }

  onSubmit(){
    console.log('Form Values', this.registrationForm.value);

  }
}
<div>

</div>
<form [formGroup]="this.registrationForm"  (ngSubmit)="onSubmit()" >
    <label>Username</label>
    <input formControlName="username" type="text"/>

    <label>Email:</label>
    <input formControlName="email" />

    <label>Password:</label>
    <input type="password" formControlName="password" />

    <button type="submit" [disabled]="this.registrationForm.invalid" >Register</button>
</form>
