<h1>NgRx Auth & Tasks</h1>
<div>
  <ng-container *ngIf="isAuthenticated$ | async; loggedOutContent">
    <p>Welcome back, <strong></strong></p>
  </ng-container>
  <ng-template #loggedOutContent>
    <p>You are not logged in.</p>
    <button (click)="loginUser()">Login</button>
  </ng-template>
</div>

<div class="container">
    <h1>📝 My Task List</h1>
    <p class="subtitle">Incomplete Tasks: <strong>{{ incompleteTasksCount$ | async }}</strong></p>

    <div class="input-section">
      <input
        [(ngModel)]="newTaskDescription"
        placeholder="Add a new task..."
        class="task-input"
      />
      <button (click)="addTask()" class="add-btn">Add</button>
    </div>

    <ul class="task-list">
      <li *ngFor="let task of (allTasks$ | async)" class="task-item">
        <label class="checkbox-wrapper">
          <input
            type="checkbox"
            [checked]="task.completed"
            (change)="toggleCompletion(task.id)"
          />
          <span [class.completed]="task.completed">{{ task.description }}</span>
        </label>
        <button (click)="removeTask(task.id)" class="delete-btn">🗑️</button>
      </li>
    </ul>
  </div>
